(function(){var tabsList=[];document.addEventListener("DOMContentLoaded",loadTabs);function loadTabs(){const tabLabels=document.querySelectorAll('.tabs__header > a');tabLabels.forEach((tabLabel)=>{const id=parseInt(tabLabel.id.replace('tab-label-',''));tabLabel.addEventListener('click',showTab);tabsList.push({label:tabLabel,content:document.querySelector('#tab-content-' + id)})})}function showTab(e){e.preventDefault();const id=parseInt(e.target.id.replace('tab-label-',''));for(ind=0;ind < tabsList.length;ind++){if(ind===id){tabsList[ind].label.classList.add('selected');tabsList[ind].content.classList.add('selected')}else{tabsList[ind].label.classList.remove('selected');tabsList[ind].content.classList.remove('selected')}}}})();